package com.example.do_an_toeic.controller.user;import com.example.do_an_toeic.dto.UserDto;import com.example.do_an_toeic.security.EntityUserDetail;import com.example.do_an_toeic.service.user.UserService;import com.example.do_an_toeic.utils.GetUser;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.annotation.AuthenticationPrincipal;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;@Controller@RequestMapping(value = {"/admin/home","/admin/", "/",""})public class HomeController {    @Autowired    private UserService userService;    @GetMapping()    public String home(Model model){        String username = GetUser.getUserName();        UserDto userDto = userService.getByUsernameDto(username);        model.addAttribute("userDto",userDto);        return "/page/index";    }}